import axios from "axios";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const DetailPage = styled.div`
  width: 100vw;
  display: flex;
  flex-direction: row;
`;

const DetailPageLeft = styled.div`
  width: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const DetailPageRight = styled.div`
  width: 50%;
  display: flex;
  flex-direction: column;
`;


export const ProductDetails = () => {
  const params = useParams(); //read doc
  const [sneakersDetails, setSneakerDetails] = useState([]); //read state

  const getSneakerDetails = async () => {
    const data = await (
      await axios.get(
        `http://localhost:13000/products/${params?.sneakerId ?? ""}`
      )
    ).data;
    setSneakerDetails(data.data);
  };
  useEffect(() => {
    getSneakerDetails();
  }, []);

  return (
    <DetailPage>
      <DetailPageLeft>
        <img src={sneakersDetails.images?.regular}/>
      </DetailPageLeft>
      <DetailPageRight>
        <h1>{sneakersDetails.name}</h1>
        <h1>{sneakersDetails.price}</h1>
        <h1></h1>
      </DetailPageRight>
    </DetailPage>
  );
};
