import axios from "axios";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const productDetailPage = styled.div`
  width: 100vw;
  display: flex;
  flex-direction: row;
`;

const detailWrapper_left = styled.div`
  width: 50%;
  display: flex; 
`;

const detailWrapper_right = styled.div`
  width: 50%;
  display: flex; 
  flex-direction: column
`;

export const ProductDetails = () => {
  const params = useParams(); //read doc
  const [sneakersDetails, setSneakerDetails] = useState([]); //read state

  const getSneakerDetails = async () => {
    const data = await (
      await axios.get(
        `http://localhost:13000/products/${params?.sneakerId ?? ""}`
      )
    ).data;
    setSneakerDetails(data.data);
  };
  useEffect(() => {
    getSneakerDetails();
  }, []);

  console.log(sneakersDetails);
  return (
    <productDetailPage
  >
        <img src={sneakersDetails.images?.regular} alt="" width="40%" />
    </productDetailPage
  >
  );
};
