import axios from "axios";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const detailPage = styled.div`
  width: 100vw;
  display: flex;
  flex-direction: row;
  align-items: center;
`;
const productDetailLeft = styled.div`
  width: 100%;
  display: flex;
`;
const productDetailRight = styled.div`
  width: 100%;
  display: flex;
`;
export const ProductDetails = () => {
  const params = useParams(); //read doc
  const [sneakersDetails, setSneakerDetails] = useState([]); //read state

  const getSneakerDetails = async () => {
    const data = await (
      await axios.get(
        `http://localhost:13000/products/${params?.sneakerId ?? ""}`
      )
    ).data;
    setSneakerDetails(data.data);
  };
  useEffect(() => {
    getSneakerDetails();
  }, []);

  return (
    <detailPage>
      <img src={sneakersDetails.images?.regular} alt=""/>
      <h1>{sneakersDetails.name}</h1>
      <h1>{sneakersDetails.price}</h1>
      <h1>{sneakersDetails.brand}</h1>
    </detailPage>
  );
};
