import React from "react";
import { Button } from "../components/Button";
import styled from "styled-components";
import e from "express";

const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  min-height: 100vh;
`;

const LoginSide = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-width:600px;
  width: 50%;
`;
const LoginCard = styled.div`
  background: #fff;
  display: flex;
  flex-direction: column;
  gap: 5px; 
  align-items: center; 
  width: 350px;
  height: 400px;
  position: relative;
  text-align: center;
  padding: 20px 0px; 
  margin: auto;
  box-shadow: 0 0 20px 0px rgb(0 0 0 / 10%);
  overflow: hidden;
`;

const LoginImage = styled.div`
  width: 50%; 
  display: flex; 
  justify-content: center; 
  align-items: center; 
`;

const LoginTitle = styled.div`
display: flex; 
justify-content: center;
width: 100px; 
border-bottom: 3px solid #001aff;;
`;

const LoginInp = styled.div`
  display: flex;
  flex-direction: column;
  width: 85%; 
  justify-content: space-between;
  padding: 10px;
`;

export const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit =  async (event) => {
    event.preventDefualt(); 
    console.log(email);
  }
  return (
    <Wrapper>
      <LoginImage>
      <img src={ require('../images/spinningshoe.gif') } width="100%" className="d-flex justify-content-center align-items-center" />
      </LoginImage>
      <LoginSide>
        <LoginCard className="shadow-lg p-3 bg-white rounded">
        <Section>
            <Wrapper>
                <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">
                    {errMsg}
                </p> 
                <h1 class="text-center">Login to Your Account</h1>
                <Form onSubmit={handleSubmit}>
                    <label htmlFor='username'>Username:</label>
                    <input 
                        type="text" 
                        id="username" 
                        ref={userRef}
                        autoComplete="off"
                        onChange={(e) => setUser(e.target.value)}
                        value={user}
                        required
                    />
                    <label htmlFor='password'>Password:</label>
                    <input 
                        type="password" 
                        id="password" 
                        onChange={(e) => setPassword(e.target.value)}
                        value={password}
                        required
                    />
                    <Button>Login</Button>
                </Form>
            </Wrapper>
        </Section>
        </LoginCard>
      </LoginSide>
    </Wrapper>
  );
};
