import { createContext, useEffect, useReducer } from "react";

export const CartContext = createContext();

export const cartReducer = (state, action) => {
    switch(action.type) {
        case 'ADD': 
            return {cart: {...action.payload, qty: 1}};
        case 'REMOVE':
            return {cart: this.cart.filter((c) => c.id === action.payload.id ? (c.qty = action.payload.qty) : c.qty)};
        default: 
            return state;
    }  
}

export const CartContextProvider = ({children}) => {
    const [state, dispatch] = useReducer(cartReducer, {
        cart: [],
        products: []
    })

    useEffect(() => {

        //checks local storage for user
        //when refresh it should still be logged in
        const cart = JSON.parse(localStorage.getItem('cart'))

        if (cart) {
            dispatch({type:'ADD', payload: cart})
        }
    }, [])

    console.log("CartContext state: ", state);

    return (
        <CartContext.Provider value={{...state, dispatch}}>
            {children}
        </CartContext.Provider>
    )
}
