import axios from "axios";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const detailWrapper = styled.div`
  width: 100vw; 
  display: flex; 
  flex: 1; 
  flex-direction: row;
`;

const detailWrapper_left = styled.div`
  width:50%; 
  display: flex; 
  justify-content: center; 
  align-items: center; 
`;

const detailWrapper_right = styled.div`
  width:50%; 
  display: flex; 
  justify-content: center; 
  align-items: center; 
`;

export const ProductDetails = () => {
  const params = useParams(); //read doc
  const [sneakersDetails, setSneakerDetails] = useState([]); //read state

  const getSneakerDetails = async () => {
    const data = await (
      await axios.get(
        `http://localhost:13000/products/${params?.sneakerId ?? ""}`
      )
    ).data;
    setSneakerDetails(data.data);
  };
  useEffect(() => {
    getSneakerDetails();
  }, []);

  console.log(sneakersDetails);
  return (
  <detailWrapper>
    <detailWrapper_left>
    <img src={sneakersDetails.images?.regular} alt="" width="40%"/>
    </detailWrapper_left>
    <detailWrapper_right>
    <h1>{sneakersDetails.name}</h1>
    <h1>{sneakersDetails.price}</h1>
    <h1>{sneakersDetails.brand}</h1>
    <h1>{sneakersDetails.colorway}</h1>
    </detailWrapper_right>
  </detailWrapper>
  );
};
