import { createContext, useReducer, useEffect, useState} from "react";
import { cartReducer } from "../hooks/useCartReducer";
import axios from "axios";

export const CartContext = createContext();

export const CartContextProvider = ({children}) => {

  const [sneakers, setSneakers] = useState([]); //read state

  const getSneakers = async () => {
    const data = await (
      await axios.get("http://localhost:13000/products")
    ).data;
    setSneakers(data.data);
  };

  useEffect(() => {
    getSneakers();
  }, []);

  const products = sneakers.map((shoe)=> ({
    id: shoe.id, 
    name: shoe.name, 
    price: shoe.price, 
    image: shoe.image.regular, 
    inStock: shoe.showInCatalog
  }))

  const [state, dispatch] = useReducer(cartReducer, {
    product: products, 
    cart: []
  })

  return (
    <CartContext.Provider value={{state, dispatch}}>
      {children}
    </CartContext.Provider>
  )
}
