import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import axios from "axios";
import About from "./pages/about";
import Account from "./pages/account";
import Contact from "./pages/contact";
import Products from "./pages/products";
import { ShoeCard } from "./components/ShoeCard";

const getSneakers = async () => {
  const sneakers = await (
    await axios.get("http://localhost:13000/sneakers")
  ).data;
  return sneakers;
};

const App = () => {
  const [sneakers, setSneakers] = useState([]);//read state 

  useEffect(async () => { //run on mount 
    const data = await getSneakers();
    setSneakers(data.data.sneakers != null ? [...data.data.sneakers] : []);
  }, []);

  return (
    <div>
      {sneakers.map((shoe) => (
        <ShoeCard name={shoe.name} img={shoe.images.regular}/>
      ))}
    </div>
  );
};

export default App;
