//import { BrowserRouter as Router, Route } from "react-router-dom";
import React, { useEffect, useState } from "react";
import {Route, Routes} from 'react-router-dom';
import {Home} from "./pages/Home";
import { ShoeCard } from "./components/ShoeCard";
import { Navbar } from "./components/Navbar";
import "./App.css";
import axios from "axios";

const App = () => {
  const [sneakers, setSneakers] = useState([]); //read state
  const [sneakerDetails, setSneakerDetails] = useState([]);

  const getSneakers = async () => {
    const data = await (
      await axios.get("http://localhost:13000/products")
    ).data;
    setSneakers(data.data);
  };

  useEffect(() => {
    getSneakers();
  }, []);
  //filter helps with giving out specific number of items
  return (
    <>
    <Navbar/>
    <div className="containter">
      <Routes>
        <Route path="/" element={<Home/>}> </Route>
        <Route path="/products" element={<Products/>}> </Route>
        {/* <Route path="/about" component={<About/>}> </Route>
        <Route path="/contact" component={<Contact/>}> </Route>
        <Route path="/admin" component={<Admin/>}> </Route> */}
      </Routes>
        <div className="products">
          {sneakers
            .filter((shoe, i) => i < 6)
            .map((shoe) => (
              <ShoeCard
                name={shoe.name}
                img={shoe.images.regular}
                id={shoe.id}
                price={shoe.price}
              />
            ))}
        </div>
    </div>
    </>
  );
};

export default App;
