      </LoginImage>
      <LoginSide>
        <LoginCard className="shadow-lg p-3 bg-white rounded">
          <LoginTitle>
            <h4 className="">Login</h4>
          </LoginTitle>
          <LoginInp>
            <input type="text" placeholder="Email"></input>
            <br></br>
            <input type="text" placeholder="Password"></input>
            <Button>Login</Button>
          </LoginInp>
        </LoginCard>
      </LoginSide>
    </Wrapper>
  );
};
