import React, { useState, useEffect } from "react";
import { ShoeCard } from "../components/ShoeCard";
import styled from "styled-components";
import axios from "axios";

const ProdPage = styled.div`
  width: 100vw;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ProdTitle = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const Prod = styled.div`
  display: flex;
  gap: 1rem;
  width: 100%;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-evenly;
`;

export const Products = () => {
  const [sneakers, setSneakers] = useState([]); //read state

  const getSneakers = async () => {
    const data = await (
      await axios.get("http://localhost:13000/products")
    ).data;
    setSneakers(data.data);
  };

  useEffect(() => {
    getSneakers();
  }, []);
  return (
    <ProdPage>
      <ProdTitle>
        <h1>All Products</h1>
      </ProdTitle>
      <div style={{ width: "100%", display: "flex", justifyContent:"center", padding: "0 2rem", boxSizing: "border-box"}}>
        <Prod>
          {sneakers
            .filter((shoe, i) => i < 10)
            .map((shoe) => (
              <ShoeCard
                name={shoe.name}
                img={shoe.images.regular}
                id={shoe.id}
                price={shoe.price}
              />
            ))}
        </Prod>
      </div>
    </ProdPage>
  );
};
