import {BrowserRouter as Router, Route} from "react-router-dom";
import React, { useEffect, useState } from "react";
import { ShoeCard } from "./components/ShoeCard";
import { getSneakers } from "./api";
import axios from "axios";

const App = () => {
  const [sneakers, setSneakers] = useState([]); //read state

  useEffect(() => {
    const getSneakers = async () => {
      const data = await (
        await axios.get("http://localhost:13000/products")
      ).data;
      setSneakers(data.data);
    };
    getSneakers()
  }, []);
  //filter((item, idx) => idx < 5)
  return (
    <div style={{display: "flex", flexDirection: "row"}}>
      {sneakers.filter(((shoe, i)=> i < 5)).map((shoe) => (
        <ShoeCard name={shoe.name} img={shoe.images.regular} id={shoe.id} price={shoe.price} />
      ))}
    </div>
  );
};

export default App;
