//import { BrowserRouter as Router, Route } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { ShoeCard } from "./components/ShoeCard";
import { getSneakers } from "./api";
import "./App.css";
import axios from "axios";

const App = () => {
  const [sneakers, setSneakers] = useState([]); //read state

  useEffect(() => {
    const getSneakers = async () => {
      const data = await (
        await axios.get("http://localhost:13000/products")
      ).data;
      setSneakers(data.data);
    };
    getSneakers();
  }, []);
  //filter helps with giving out specific number of items
  return (
    <div className="containter">
      <div className="slide">
        <div className="top-slide-section"></div>
        <div className="bottom-slide-section"></div>
      </div>
      <div>
        <div className="Featured-Products">
          <h1> Featured Products</h1>
        </div>
        <div style={{ display: "flex", flexDirection: "row" }}>
          {sneakers
            .filter((shoe, i) => i < 6)
            .map((shoe) => (
              <ShoeCard
                name={shoe.name}
                img={shoe.images.regular}
                id={shoe.id}
                price={shoe.price}
              />
            ))}
        </div>
      </div>
      <div className="bottom-page"></div>
    </div>
  );
};

export default App;
