//like sign up and login but instead it is inventory and sneaker
import React, { useState } from "react";
import { useLogout } from "../hooks/useLogout";
import { Container, Nav, Button} from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { Inventory } from "../components/Inventory";


export const Account = () => {
    const {logout} = useLogout(); //for logout button
    const [toggle, setToggle] = useState(false);

    const handleClick  = () => {
      logout();
    }
  return (
    <Container className="vh-100%">
        <span className="d-flex flex-row justify-content-end">
            <Button onClick={() => setToggle(!toggle)}>Inventory</Button>
            <Button onClick={() => setToggle(!toggle)}>Sneakers</Button>
            <Button className="mx">
              <Nav.Link to="/inventory" as={NavLink}>
                Inventory
              </Nav.Link>
            </Button>
            <Button className="mx-3">
              <Nav.Link to="/sneakers" as={NavLink}>
                Sneakers
              </Nav.Link>
            </Button>
            <Button onClick={handleClick}>Log Out</Button>
        </span>
        <h1>Welcome back, DirtySoles!</h1>
        <div className="d-flex">
            {toggle && <Inventory />}
            {toggle && <div>Sneakers</div>}
        </div>
    </Container>       
  )
}
