import { useCartContext } from "./useCartContext"

export const useRemove = () => {
    const { dispatch } = useCartContext(); 

    const remove = () => {
        //removes user from local storage
        localStorage.removeItem('cart');

        //dispatch logout action 
        dispatch({type: 'REMOVE'});
    }   
    return {remove};
}
